import React from 'react';

const products = [
  { name: "Product 1", image: "https://d1tgh8fmlzexmh.cloudfront.net/ccbp-responsive-website/ecommerce-services-delivery-img.png" },
  { name: "Product 2", image: "" },
  { name: "Product 3", image: "/F" },
  { name: "Product 4", image: "" },
  { name: "Product 5", image: "" }
];

const ProductList = ({ cart, setCart }) => {
  const addToCart = (product) => {
    const updatedCart = [...cart, { product: product.name, quantity: 1 }];
    setCart(updatedCart);
  };

  return (
    <div>
      {products.map((product, index) => (
        <div key={index} style={{ marginBottom: "20px" }}>
          <img src={product.image} alt={product.name} width="200" />
          <p>{product.name}</p>
          <button onClick={() => addToCart(product)}>Add to Cart</button>
        </div>
      ))}
    </div>
  );
};

export default ProductList;
