import React from 'react';
import './ProductList.css'; // Import CSS file

const products = [
  { name: "Product 1", image: "https://d1tgh8fmlzexmh.cloudfront.net/ccbp-responsive-website/ecommerce-services-delivery-img.png" },
  { name: "Product 2", image: "https://d1tgh8fmlzexmh.cloudfront.net/ccbp-responsive-website/ecommerce-services-delivery-img.png" },
  { name: "Product 3", image: "https://d1tgh8fmlzexmh.cloudfront.net/ccbp-responsive-website/ecommerce-services-delivery-img.png" },
  { name: "Product 4", image: "https://d1tgh8fmlzexmh.cloudfront.net/ccbp-responsive-website/ecommerce-services-delivery-img.png" },
  { name: "Product 5", image: "https://d1tgh8fmlzexmh.cloudfront.net/ccbp-responsive-website/ecommerce-services-delivery-img.png" }
];

const ProductList = ({ cart, setCart }) => {
  const addToCart = (product) => {
    const updatedCart = [...cart, { product: product.name, quantity: 1 }];
    setCart(updatedCart);
  };

  return (
    <div className="product-container">
      {products.map((product, index) => (
        <div key={index} className="product-card">
          <img src={product.image} alt={product.name} className="product-image" />
          <p className="product-name">{product.name}</p>
          <button className="add-to-cart-btn" onClick={() => addToCart(product)}>
            Add to Cart
          </button>
        </div>
      ))}
    </div>
  );
};

export default ProductList;
